function popUp(winURL){window.open(winURL, "popup", "width=320, height=480");}
window.onload=prepareLinks;
function prepareLinks(){if(!document.getElementsByTagName)return false;
var a=document.getElementsByTagName("a");
for(var i=0;i<a.length;i++){if(a[i].getAttribute("class")=="popup"){a[i].onclick=function(){
popUp(this.getAttribute("href"));return false;}}}}